        checkColor(count){
            return count ? 'red' : 'green'
        },
        async getPullData(){
            var reqBody = {
                'entity_id': 'GKI',
                'appl_id': 'WEBGK',
                'menu_header_caption': 'Ledger'
            }

            const respData = await menu.menu_item('', reqBody, false, false, false)

            if (respData.status === 200) {
                this.menus = respData.data
            }
        }
    }
